import React, { useState } from "react";
import "./styles.css";

import { fabric } from "fabric";
// Or you can use:
// const fabric = require("fabric").fabric;

const App = () => {
  const [gCanvas, setCanvas] = useState(null);
  React.useEffect(() => {
    //setCanvas(new fabric.Canvas("my-fabric-canvas"));
    const canvas = new fabric.Canvas(canvasRef);
    setCanvas((gCanvas) => canvas);
    const rect = new fabric.Rect({
      width: 50,
      height: 50,
      fill: "blue",
      angle: 10,
      top: 20,
      left: 20,
    });
    const textbox = new fabric.Textbox("Click on the Rectangle to move it.", {
      fontSize: 20,
      left: 50,
      top: 100,
      width: 200,
    });
    canvas.add(textbox);
    canvas.add(rect);

    // UseEffect's cleanup function
    return () => {
      canvas.dispose();
      setCanvas(null);
    };
  }, []);

  const handleMouseClick = (event) => {
    var pointer = gCanvas.getPointer(event);
    console.log(pointer);
  };
  const handleMouseMove = (event) => {};

  return (
    <div className="App">
      <canvas
        ref={canvasRef}
        onMouseMove={handleMouseMove}
        onClick={handleMouseClick}
        id="my-fabric-canvas"
        width="400"
        height="300"
      />
    </div>
  );
};

export default App;
