import "./styles.css";
import styles from "./rightMenu.module.css";
import { fabric } from "fabric";

const initialState = {
  version: "3.6.3",
  objects: [
    {
      type: "rect",
      version: "3.6.3",
      originX: "left",
      originY: "top",
      left: 106,
      top: 33,
      width: 60,
      height: 70,
