const App = () => {
  const canvasRef = useRef();
  const [canvas, setCanvas] = useState();
  const [canvasHistory, setCanvasHistory] = useState([initialState]);
  const [canvasState, setCanvasState] = useState(initialState);
  const [canvasTool, setCanvasTool] = useState("select");

  var nRect = true;
  var Rectangle = null;
  var origX, origY;

  const onObjectModified = useCallback(
    (e) => {
      const newCanvasState = e.target.canvas.toJSON();
      setCanvasState(newCanvasState);
      // Limit history depth
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
    },
    [setCanvasState, setCanvasHistory]
  );

  React.useEffect(() => {
    //console.log(JSON.stringify(canvasState));
  }, [canvasState]);

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.loadFromJSON(initialState);
    //canvas.on("object:added", onObjectModified);
    canvas.on("object:modified", onObjectModified);

    //canvas.on("object:removed", onObjectModified);

    const rect = new fabric.Rect({
      width: 50,
      height: 50,
      fill: "blue",
      angle: 10,
      top: 20,
      left: 20,
    });
    const textbox = new fabric.Textbox("Click on the Rectangle to move it.", {
      fontSize: 20,
      left: 50,
      top: 100,
      width: 200,
    });
    canvas.add(textbox);
    canvas.add(rect);
    setCanvas(canvas);
    //refCanvas.current = canvas;
    // UseEffect's cleanup function
    return () => {
      canvas.dispose();
      //setCanvas(null);
    };
  }, [canvasRef, onObjectModified, setCanvas]);

  const moveHistory = useCallback(
    (step) => {
      const currentStateIndex = canvasHistory.indexOf(canvasState);
      const prevState = canvasHistory[currentStateIndex + step];
      canvas.loadFromJSON(prevState);
      setCanvasState(prevState);
    },
    [canvas, canvasState, canvasHistory, setCanvasState]
  );

  const onUndo = useCallback(() => moveHistory(-1), [moveHistory]);

  const onRedo = useCallback(() => moveHistory(1), [moveHistory]);

  const handleMouseClick = (event) => {
    //let o = canvas.getActiveObject();
    //console.log(o);
    if (canvasTool === "select") {
    }
    if (canvasTool === "line") {
    }
    if (canvasTool === "rect") {
      if (nRect) {
        var pointer = canvas.getPointer(event);
        origX = pointer.x;
        origY = pointer.y;
        Rectangle = new fabric.Rect({
          left: origX,
          top: origY,
          fill: "transparent",
          stroke: "red",
          strokeWidth: 3,
        });
        //console.log(Rectangle);
        canvas.add(Rectangle);
        setCanvas(canvas);
        //canvas.renderAll();
        //setRect((prevState) => (prevState = false));
        nRect = false;
        //setNewRectangle(false);
      } else {
        pointer = canvas.getPointer(event);
        if (origX > pointer.x) {
          Rectangle.set({ left: Math.abs(pointer.x) });
        }
        if (origY > pointer.y) {
          Rectangle.set({ top: Math.abs(pointer.y) });
        }
        Rectangle.set({ width: Math.abs(origX - pointer.x) });
        Rectangle.set({ height: Math.abs(origY - pointer.y) });
        canvas.setActiveObject(Rectangle);
        //setNewRectangle(true);
        nRect = true;
        // Render the Canvas
        canvas.renderAll();

        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
  };
  const handleMouseMove = (event) => {
    if (nRect) return;
    //canvas.renderAll();
    var pointer = canvas.getPointer(event);
    if (origX > pointer.x) {
      Rectangle.set({ left: Math.abs(pointer.x) });
    }
    if (origY > pointer.y) {
      Rectangle.set({ top: Math.abs(pointer.y) });
    }
    Rectangle.set({ width: Math.abs(origX - pointer.x) });
    Rectangle.set({ height: Math.abs(origY - pointer.y) });

    //console.log(Rectangle);
    canvas.renderAll();

    //var pointer = canvas.getPointer(event);
    //console.log(pointer);
  };

  return (
    <div
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
      className="App"
    >
      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
      <button onClick={onUndo} disabled={canvasHistory[0] === canvasState}>
        Undo
      </button>
      <button
        onClick={onRedo}
        disabled={canvasHistory[canvasHistory.length - 1] === canvasState}
      >
        Redo
      </button>
      <div className={styles.right}>
        <div className={styles.tab}>
          <button
            onClick={(e) => {
              setCanvasTool("select");
            }}
            id="defaultSelect"
          >
            <div className={styles.tooltip}>
              <img src={"images/Select2.png"} alt="Select"></img>
              <span className={styles.tooltiptext}>Selection</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("text");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/TextBox2.png"} alt="TextBox"></img>
              <span className={styles.tooltiptext}>Text</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("pen");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/PenTool2.png"} alt="Pen"></img>
              <span className={styles.tooltiptext}>Free-hand</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("cloud");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Cloud2.png"} alt="Cloud"></img>
              <span className={styles.tooltiptext}>Cloud</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("callout");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/CallOut2.png"} alt="Callout"></img>
              <span className={styles.tooltiptext}>Callout</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("image");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Image2.png"} alt="Image Edit"></img>
              <span className={styles.tooltiptext}>Image Edit</span>
            </div>
          </button>
          <div className={styles.tabspace}></div>
          <button
            onClick={(e) => {
              setCanvasTool("line");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Line2.png"} alt="Line"></img>
              <span className={styles.tooltiptext}>Line</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("polyline");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Polyline2.png"} alt="Polyline"></img>
              <span className={styles.tooltiptext}>Polyline</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("arrow");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Arrow2.png"} alt="Arrow"></img>
              <span className={styles.tooltiptext}>Arrow</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("arc");
