        return;
      }

      if (canvasTool === "rect") {
        pointer.current = canvas.getPointer(opt);

        if (origX.current > pointer.current.x) {
          Rectangle.current.set({
            left: Math.abs(pointer.current.x)
          });
        }

        if (origY.current > pointer.current.y) {
