        angle.current = Math.atan2(dy, dx);
        angle.current *= 180 / Math.PI;
        angle.current += 90;
        arrow.current.set({
          top: line.current.y2,
          left: line.current.x2,
          angle: angle.current,
          width: 15,
          height: 15
        });
