import React, { useRef, useState, useCallback } from "react";
import "./styles.css";

import { fabric } from "fabric";

const initialState = {
  version: "3.6.3",
  objects: [
    {
      type: "rect",
      version: "3.6.3",
      originX: "left",
      originY: "top",
      left: 106,
      top: 33,
      width: 60,
      height: 70,
      fill: "red",
      stroke: null,
      strokeWidth: 1,
      strokeDashArray: null,
      strokeLineCap: "butt",
      strokeDashOffset: 0,
      strokeLineJoin: "miter",
      strokeMiterLimit: 4,
      scaleX: 1,
      scaleY: 1,
      angle: 0,
      flipX: false,
      flipY: false,
      opacity: 1,
      shadow: null,
      visible: true,
      clipTo: null,
      backgroundColor: "",
      fillRule: "nonzero",
      paintFirst: "fill",
      globalCompositeOperation: "source-over",
      transformMatrix: null,
      skewX: 0,
      skewY: 0,
      rx: 0,
      ry: 0,
    },
  ],
};

// Or you can use:
// const fabric = require("fabric").fabric;
const canvas = new fabric.Canvas("my-fabric-canvas");
const App = () => {
  const canvasRef = useRef();
  const [canvas, setCanvas] = useState();
  const [canvasHistory, setCanvasHistory] = useState([initialState]);
  const [canvasState, setCanvasState] = useState(initialState);
  //const [mX, setX] = useState(0);
  //const [mY, setY] = useState(0);

  //const refCanvas = useRef < fabric.Canvas > null;
  //const [Rectangles, setRectangles] = useState([]);
  //const [NewRectangle, setNewRectangle] = useState(true);
  //const canvasRef = useRef();
  var nRect = true;
  var Rectangle = null;
  var origX, origY;
  //const [gCanvas, setCanvas] = useState(null);
  //const canvas = new fabric.Canvas("my-fabric-canvas");
  //const// canvas = new fabric.Canvas("my-fabric-canvas");

  const onObjectModified = useCallback(
    (e) => {
      const newCanvasState = e.target.canvas.toJSON();
      setCanvasState(newCanvasState);
      // Limit history depth
      setCanvasHistory((history) => [...history, newCanvasState].slice(-4));
    },
    [setCanvasState, setCanvasHistory]
  );

  React.useEffect(() => {
    console.log(JSON.stringify(canvasState));
  }, [canvasState]);

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.loadFromJSON(initialState);
    canvas.on("object:modified", onObjectModified);
    //const canvas = canvasRef.current;
    //setCanvas(new fabric.Canvas("my-fabric-canvas"));
    //const canvas = new fabric.Canvas("my-fabric-canvas");
    //setCanvas((gCanvas) => canvas);
    const rect = new fabric.Rect({
      width: 50,
      height: 50,
      fill: "blue",
      angle: 10,
      top: 20,
      left: 20,
    });
    const textbox = new fabric.Textbox("Click on the Rectangle to move it.", {
      fontSize: 20,
      left: 50,
      top: 100,
      width: 200,
    });
    canvas.add(textbox);
    canvas.add(rect);
    setCanvas(canvas);
    //refCanvas.current = canvas;
    // UseEffect's cleanup function
    return () => {
      canvas.dispose();
      //setCanvas(null);
    };
  }, [canvasRef, onObjectModified, setCanvas]);

  const moveHistory = useCallback(
    (step) => {
      const currentStateIndex = canvasHistory.indexOf(canvasState);
      const prevState = canvasHistory[currentStateIndex + step];
      canvas.loadFromJSON(prevState);
      setCanvasState(prevState);
    },
    [canvas, canvasState, canvasHistory, setCanvasState]
  );

  const onUndo = useCallback(() => moveHistory(-1), [moveHistory]);

  const onRedo = useCallback(() => moveHistory(1), [moveHistory]);

  const handleMouseClick = (event) => {
    //return;
    //canvas = new fabric.Canvas("my-fabric-canvas");
    //console.log(event.clientX);
    if (nRect) {
      var pointer = canvas.getPointer(event);
      origX = pointer.x;
      origY = pointer.y;
      Rectangle = new fabric.Rect({
        left: origX,
        top: origY,
        fill: "transparent",
        stroke: "red",
        strokeWidth: 3,
      });
      //console.log(Rectangle);
      canvas.add(Rectangle);
      //canvas.renderAll();
      nRect = false;
      //setNewRectangle(false);
    } else {
      pointer = canvas.getPointer(event);
      if (origX > pointer.x) {
        Rectangle.set({ left: Math.abs(pointer.x) });
      }
      if (origY > pointer.y) {
        Rectangle.set({ top: Math.abs(pointer.y) });
      }
      Rectangle.set({ width: Math.abs(origX - pointer.x) });
      Rectangle.set({ height: Math.abs(origY - pointer.y) });
      //console.log(Rectangle);
      canvas.renderAll();
      setCanvas(canvas);
      nRect = true;
      //setNewRectangle(true);
    }

    //console.log(pointer);
  };
  const handleMouseMove = (event) => {
    if (nRect) return;
    //canvas.renderAll();
    var pointer = canvas.getPointer(event);
    if (origX > pointer.x) {
      Rectangle.set({ left: Math.abs(pointer.x) });
    }
    if (origY > pointer.y) {
      Rectangle.set({ top: Math.abs(pointer.y) });
    }
    Rectangle.set({ width: Math.abs(origX - pointer.x) });
    Rectangle.set({ height: Math.abs(origY - pointer.y) });
    //console.log(Rectangle);
    canvas.renderAll();
    //var pointer = canvas.getPointer(event);
    //console.log(pointer);
  };

  return (
    <div
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
      className="App"
    >
      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
      <button onClick={onUndo} disabled={canvasHistory[0] === canvasState}>
        Undo
      </button>
      <button
        onClick={onRedo}
        disabled={canvasHistory[canvasHistory.length - 1] === canvasState}
      >
        Redo
      </button>
      <canvas ref={canvasRef} id="my-fabric-canvas" width="1000" height="800" />
    </div>
  );
};

export default App;
