        var option = document.createElement("option");
        option.text = font;
        x.add(option);
      }
      //console.log("Available Fonts:", [...fontAvailable.values()]);
    }
  }, []);
  React.useEffect(() => {}, [canvasState]);

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.loadFromJSON(initialState);
    //canvas.on("object:added", onObjectModified);
    canvas.on("object:modified", onObjectModified);
    canvas.on("mouse:up", function (e) {
      this.setViewportTransform(this.viewportTransform);
      this.isDragging = false;
      this.selection = true;
      if (canvas.isDrawingMode) {
        canvas.renderAll();
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
        // }
      }
      //check if user clicked an object
      if (e.target) {
        var activeShape = canvas.getActiveObject();
        if (activeShape) {
          setFillColor(activeShape.fill);
          setStrokeColor(activeShape.stroke);
          setStrokeWidth(activeShape.strokeWidth);
          setOpacity(activeShape.opacity);
          setFont(activeShape.fontFamily);
          setFontSize(activeShape.fontSize);
          setFontWeight(activeShape.fontWeight);
          setFontStyle(activeShape.fontStyle);
        }
      } else {
        //add rectangle
        //addrect(e.e.clientY, e.e.clientX, 100, 100, "#CCCCC");
      }
    });

    canvas.on("mouse:wheel", function (opt) {
      /*
      previous codes
      var delta = opt.e.deltaY;
  var zoom = canvas.getZoom();
  zoom *= 0.999 ** delta;
  if (zoom > 20) zoom = 20;
  if (zoom < 0.01) zoom = 0.01;
  canvas.zoomToPoint({ x: opt.e.offsetX, y: opt.e.offsetY }, zoom);
  opt.e.preventDefault();
  opt.e.stopPropagation();
      */
      var delta = opt.e.deltaY;
      var zoom = canvas.getZoom();
      zoom *= 0.999 ** delta;
      if (zoom > 20) zoom = 20;
      if (zoom < 0.01) zoom = 0.01;
      canvas.zoomToPoint({ x: opt.e.offsetX, y: opt.e.offsetY }, zoom);
      opt.e.preventDefault();
      opt.e.stopPropagation();
      var vpt = this.viewportTransform;
      if (zoom < 400 / 1000) {
        vpt[4] = 200 - (1000 * zoom) / 2;
        vpt[5] = 200 - (1000 * zoom) / 2;
      } else {
        if (vpt[4] >= 0) {
          vpt[4] = 0;
        } else if (vpt[4] < canvas.getWidth() - 1000 * zoom) {
          vpt[4] = canvas.getWidth() - 1000 * zoom;
        }
        if (vpt[5] >= 0) {
          vpt[5] = 0;
        } else if (vpt[5] < canvas.getHeight() - 1000 * zoom) {
          vpt[5] = canvas.getHeight() - 1000 * zoom;
        }
      }
    });
    canvas.on("mouse:down", function (opt) {
      var evt = opt.e;
      if (evt.altKey === true) {
        this.isDragging = true;
        this.selection = false;
        this.lastPosX = evt.clientX;
        this.lastPosY = evt.clientY;
      }
    });
    canvas.on("mouse:move", function (opt) {
      if (this.isDragging) {
        var e = opt.e;
        var vpt = this.viewportTransform;
        vpt[4] += e.clientX - this.lastPosX;
        vpt[5] += e.clientY - this.lastPosY;
        this.requestRenderAll();
        this.lastPosX = e.clientX;
        this.lastPosY = e.clientY;
      }
    });
    /*
    canvas.on("object:added", function () {
