      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
    }
    if (canvasTool === "line") {
      if (newShape) {
        pointer = canvas.getPointer(event);
        var points = [pointer.x, pointer.y, pointer.x, pointer.y];
        line = new fabric.Line(points, {
          strokeWidth: strokeWidth,
          fill: fill,
          stroke: stroke,
          originX: "center",
          originY: "center",
        });
        canvas.add(line);
        newShape = false;
      } else {
        pointer = canvas.getPointer(event);
        line.set({ x2: pointer.x, y2: pointer.y });

        canvas.setActiveObject(line);
        canvas.renderAll();
        newShape = true;
        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
    if (canvasTool === "arrow") {
      if (newShape) {
        newShape = false;
        pointer = canvas.getPointer(event);
        points = [pointer.x, pointer.y, pointer.x, pointer.y];
        line = new fabric.Line(points, {
          strokeWidth: strokeWidth,
          fill: fill,
          stroke: stroke,
          originX: "center",
          originY: "center",
          //id: "arrow_line",
          //uuid: generateUUID(),
          type: "arrow",
        });
        var centerX = (line.x1 + line.x2) / 2;
        var centerY = (line.y1 + line.y2) / 2;
        deltaX = line.left - centerX;
        deltaY = line.top - centerY;
        triangle = new fabric.Triangle({
          left: line.get("x1") + deltaX,
          top: line.get("y1") + deltaY,
          originX: "center",
          originY: "center",
          selectable: false,
          pointType: "arrow_start",
          angle: -45,
          width: 20,
          height: 20,
          fill: fill,
          //id: "arrow_triangle",
          //uuid: line.uuid,
        });
        canvas.add(line, triangle);
      } else {
        pointer = canvas.getPointer(event);
        line.set({
          x2: pointer.x,
          y2: pointer.y,
        });
        triangle.set({
          left: pointer.x + deltaX,
          top: pointer.y + deltaY,
          angle: _FabricCalcArrowAngle(line.x1, line.y1, line.x2, line.y2),
        });
        var group = new window.fabric.Group([line, triangle], {
          borderColor: "black",
          cornerColor: "green",
          lockScalingFlip: true,
          typeOfGroup: "arrow",
          userLevel: 1,
          name: "my_ArrowGroup",
          //uuid : activeObj.uuid,
          type: "arrow",
        });
        canvas.remove(line, triangle); // removing old object
        //activeObj = group;
        canvas.add(group);
        canvas.setActiveObject(group);
        //setNewRectangle(true);
        newShape = true;
        // Render the Canvas
        canvas.renderAll();

        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
    if (canvasTool === "rect") {
      if (newShape) {
        pointer = canvas.getPointer(event);
        origX = pointer.x;
        origY = pointer.y;
        Rectangle = new fabric.Rect({
          left: origX,
          top: origY,
          fill: fill,
          stroke: stroke,
          strokeWidth: strokeWidth,
          strokeDashArray: [dashSize, gapSize],
          opacity: opacity,
        });
        //console.log(Rectangle);
        canvas.add(Rectangle);
        setCanvas(canvas);
        //canvas.renderAll();
        //setRect((prevState) => (prevState = false));
        newShape = false;
        //setNewRectangle(false);
      } else {
        pointer = canvas.getPointer(event);
        if (origX > pointer.x) {
          Rectangle.set({ left: Math.abs(pointer.x) });
        }
        if (origY > pointer.y) {
          Rectangle.set({ top: Math.abs(pointer.y) });
        }
        Rectangle.set({ width: Math.abs(origX - pointer.x) });
        Rectangle.set({ height: Math.abs(origY - pointer.y) });
        canvas.setActiveObject(Rectangle);
        //setNewRectangle(true);
        newShape = true;
        // Render the Canvas
        canvas.renderAll();

        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
    if (canvasTool === "circle") {
      if (newShape) {
        pointer = canvas.getPointer(event);
        origX = pointer.x;
        origY = pointer.y;
        circle = new fabric.Circle({
          left: origX,
          top: origY,
          originX: "left",
          originY: "top",
          radius: pointer.x - origX,
          angle: 0,
          fill: fill,
          stroke: stroke,
          strokeWidth: strokeWidth,
        });
        canvas.add(circle);
        newShape = false;
      } else {
        pointer = canvas.getPointer(event);
        var radius =
          Math.max(Math.abs(origY - pointer.y), Math.abs(origX - pointer.x)) /
          2;
        if (radius > circle.strokeWidth) {
          radius -= circle.strokeWidth / 2;
        }
        circle.set({ radius: radius });

        if (origX > pointer.x) {
          circle.set({ originX: "right" });
        } else {
          circle.set({ originX: "left" });
        }
        if (origY > pointer.y) {
          circle.set({ originY: "bottom" });
        } else {
          circle.set({ originY: "top" });
        }
        canvas.setActiveObject(circle);
        //setNewRectangle(true);
        newShape = true;
        // Render the Canvas
        canvas.renderAll();
        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
    if (canvasTool === "ellipse") {
      if (newShape) {
        pointer = canvas.getPointer(event);
        origX = pointer.x;
        origY = pointer.y;
        ellipse = new fabric.Ellipse({
          left: origX,
          top: origY,
          originX: "left",
          originY: "top",
          rx: pointer.x - origX,
          ry: pointer.y - origY,
          angle: 0,
          fill: fill,
          stroke: stroke,
          strokeWidth: strokeWidth,
          type: "ellipse",
          //uuid: generateUUID(),
        });
        canvas.add(ellipse);
        newShape = false;
        //activeObj = ellipse;
      } else {
        pointer = canvas.getPointer(event);
        var rx = Math.abs(origX - pointer.x) / 2;
        var ry = Math.abs(origY - pointer.y) / 2;
        if (rx > ellipse.strokeWidth) {
          rx -= ellipse.strokeWidth / 2;
        }
        if (ry > ellipse.strokeWidth) {
          ry -= ellipse.strokeWidth / 2;
        }
        ellipse.set({ rx: rx, ry: ry });

        if (origX > pointer.x) {
          ellipse.set({ originX: "right" });
        } else {
          ellipse.set({ originX: "left" });
        }
        if (origY > pointer.y) {
          ellipse.set({ originY: "bottom" });
        } else {
          ellipse.set({ originY: "top" });
        }
        canvas.setActiveObject(ellipse);
        //setNewRectangle(true);
        newShape = true;
        // Render the Canvas
        canvas.renderAll();

        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
  };
  const handleMouseMove = (event) => {
    if (newShape === true && canvasTool !== "pen") return;
    //canvas.renderAll();
    if (canvasTool === "rect") {
      var pointer = canvas.getPointer(event);
      if (origX > pointer.x) {
        Rectangle.set({ left: Math.abs(pointer.x) });
      }
      if (origY > pointer.y) {
        Rectangle.set({ top: Math.abs(pointer.y) });
      }
      Rectangle.set({ width: Math.abs(origX - pointer.x) });
      Rectangle.set({ height: Math.abs(origY - pointer.y) });

      canvas.renderAll();
    }
    if (canvasTool === "line") {
      pointer = canvas.getPointer(event);
      line.set({ x2: pointer.x, y2: pointer.y });
      canvas.renderAll();
    }
    if (canvasTool === "arrow") {
      pointer = canvas.getPointer(event);
      line.set({
        x2: pointer.x,
        y2: pointer.y,
      });
      triangle.set({
        left: pointer.x + deltaX,
        top: pointer.y + deltaY,
        angle: _FabricCalcArrowAngle(line.x1, line.y1, line.x2, line.y2),
      });
      canvas.renderAll();
    }

    if (canvasTool === "circle") {
      pointer = canvas.getPointer(event);
      var radius =
        Math.max(Math.abs(origY - pointer.y), Math.abs(origX - pointer.x)) / 2;
      if (radius > circle.strokeWidth) {
        radius -= circle.strokeWidth / 2;
      }
      circle.set({ radius: radius });

      if (origX > pointer.x) {
        circle.set({ originX: "right" });
      } else {
        circle.set({ originX: "left" });
      }
      if (origY > pointer.y) {
        circle.set({ originY: "bottom" });
      } else {
        circle.set({ originY: "top" });
      }
      // Render the Canvas
      canvas.renderAll();
    }
    if (canvasTool === "ellipse") {
      pointer = canvas.getPointer(event);
      var rx = Math.abs(origX - pointer.x) / 2;
      var ry = Math.abs(origY - pointer.y) / 2;
      if (rx > ellipse.strokeWidth) {
        rx -= ellipse.strokeWidth / 2;
      }
      if (ry > ellipse.strokeWidth) {
        ry -= ellipse.strokeWidth / 2;
      }
