    //console.log(colors);
  }

  function closeHandler(colors) {
    if (colors.color !== fill) {
      console.log(colors);
      var rgba = hexToRGB(colors.color, colors.alpha / 100);
      setFillColor(rgba);
      setShapeAttributes({ fill: rgba });
    }
  }
  function openStrokeHandler(colors) {
    setStrokeColor(colors.color);
  }
  function closeStrokeHandler(colors) {
    if (colors.color !== stroke) {
      var rgba = hexToRGB(colors.color, colors.alpha / 100);
      setStrokeColor(rgba);
      setShapeAttributes({ stroke: rgba });
    }
  }
  function freeHand(event) {
    setCanvasTool("pen");
    canvas.isDrawingMode = 1;
    canvas.freeDrawingBrush.color = stroke;
    canvas.freeDrawingBrush.width = strokeWidth;
    canvas.renderAll();
  }

  function deleteObjects() {
    canvas.getActiveObjects().forEach((obj) => {
      canvas.remove(obj);
    });
    canvas.discardActiveObject().renderAll();
    const newCanvasState = canvas.toJSON();
    setCanvasState(newCanvasState);
    // Save state to History and Limit history depth to 32
    setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
  }
  function hexToRGB(hex, alpha) {
    var r = parseInt(hex.slice(1, 3), 16),
      g = parseInt(hex.slice(3, 5), 16),
      b = parseInt(hex.slice(5, 7), 16);

    if (alpha) {
      return "rgba(" + r + ", " + g + ", " + b + ", " + alpha + ")";
    } else {
      return "rgb(" + r + ", " + g + ", " + b + ")";
    }
  }

  let url = "#";
  return (
    <div
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
