import "rc-color-picker/assets/index.css";
import React, { useRef, useState, useCallback } from "react";
////import { PopoverPicker } from "./PopoverPicker";

import { fabric } from "fabric";
import "./styles.css";
import styles from "./rightMenu.module.css";
import tbStyles from "./toolbar.module.css";
//import { RgbaColorPicker } from "react-colorful";
import ColorPicker from "rc-color-picker";
//import "react-colorful/dist/index.css";

const initialState = {
  version: "3.6.3",
  objects: [
    {
      type: "rect",
      version: "3.6.3",
      originX: "left",
      originY: "top",
      left: 0,
      top: 0,
      width: 1,
      height: 1,
      fill: "black",
      stroke: null,
      strokeWidth: 1,
      strokeDashArray: null,
      strokeLineCap: "butt",
      strokeDashOffset: 0,
      strokeLineJoin: "miter",
      strokeMiterLimit: 4,
      scaleX: 1,
      scaleY: 1,
      angle: 0,
      flipX: false,
      flipY: false,
      opacity: 1,
      shadow: null,
      visible: false,
      clipTo: null,
      backgroundColor: "",
      fillRule: "nonzero",
      paintFirst: "fill",
      globalCompositeOperation: "source-over",
      transformMatrix: null,
      skewX: 0,
      skewY: 0,
      rx: 0,
      ry: 0,
    },
  ],
};

const fontCheck = new Set(
  [
    // Windows 10
    "Arial",
    "Arial Black",
    "Bahnschrift",
    "Calibri",
    "Cambria",
    "Cambria Math",
    "Candara",
    "Comic Sans MS",
    "Consolas",
    "Constantia",
    "Corbel",
    "Courier New",
    "Ebrima",
    "Franklin Gothic Medium",
    "Gabriola",
    "Gadugi",
    "Georgia",
    "HoloLens MDL2 Assets",
    "Impact",
    "Ink Free",
    "Javanese Text",
    "Leelawadee UI",
    "Lucida Console",
    "Lucida Sans Unicode",
    "Malgun Gothic",
    "Marlett",
    "Microsoft Himalaya",
    "Microsoft JhengHei",
    "Microsoft New Tai Lue",
    "Microsoft PhagsPa",
    "Microsoft Sans Serif",
    "Microsoft Tai Le",
    "Microsoft YaHei",
    "Microsoft Yi Baiti",
    "MingLiU-ExtB",
    "Mongolian Baiti",
    "MS Gothic",
    "MV Boli",
    "Myanmar Text",
    "Nirmala UI",
    "Palatino Linotype",
    "Segoe MDL2 Assets",
    "Segoe Print",
    "Segoe Script",
    "Segoe UI",
    "Segoe UI Historic",
    "Segoe UI Emoji",
    "Segoe UI Symbol",
    "SimSun",
    "Sitka",
    "Sylfaen",
    "Symbol",
    "Tahoma",
    "Times New Roman",
    "Trebuchet MS",
    "Verdana",
    "Webdings",
    "Wingdings",
    "Yu Gothic",
    // macOS
    "American Typewriter",
    "Andale Mono",
    "Arial",
    "Arial Black",
    "Arial Narrow",
    "Arial Rounded MT Bold",
    "Arial Unicode MS",
    "Avenir",
    "Avenir Next",
    "Avenir Next Condensed",
    "Baskerville",
    "Big Caslon",
    "Bodoni 72",
    "Bodoni 72 Oldstyle",
    "Bodoni 72 Smallcaps",
    "Bradley Hand",
    "Brush Script MT",
    "Chalkboard",
    "Chalkboard SE",
    "Chalkduster",
    "Charter",
    "Cochin",
    "Comic Sans MS",
    "Copperplate",
    "Courier",
    "Courier New",
    "Didot",
    "DIN Alternate",
    "DIN Condensed",
    "Futura",
    "Geneva",
    "Georgia",
    "Gill Sans",
    "Helvetica",
    "Helvetica Neue",
    "Herculanum",
    "Hoefler Text",
    "Impact",
    "Lucida Grande",
    "Luminari",
    "Marker Felt",
    "Menlo",
    "Microsoft Sans Serif",
    "Monaco",
    "Noteworthy",
    "Optima",
    "Palatino",
    "Papyrus",
    "Phosphate",
    "Rockwell",
    "Savoye LET",
    "SignPainter",
    "Skia",
    "Snell Roundhand",
    "Tahoma",
    "Times",
    "Times New Roman",
    "Trattatello",
    "Trebuchet MS",
    "Verdana",
    "Zapfino",
  ].sort()
);
// Or you can use:
// const fabric = require("fabric").fabric;
//const canvas = new fabric.Canvas("my-fabric-canvas");
const App = () => {
  const canvasRef = useRef();
  const shapeCount = useRef(0);
  const isDrawing = useRef(false);
  const currentStroke = useRef(null);
  const editPoly = useRef(true);
  const openPoly = useRef(null);
  const polyType = useRef("");
  //anchors
  const [canvas, setCanvas] = useState();
  const [canvasHistory, setCanvasHistory] = useState([initialState]);
  //const [canvasHistory, setCanvasHistory] = useState([]);
  const [canvasState, setCanvasState] = useState(initialState);
  //const [canvasState, setCanvasState] = useState();
  const [canvasTool, setCanvasTool] = useState("select");
  const [strokeWidth, setStrokeWidth] = useState(1);
  const [stroke, setStrokeColor] = useState("black");
  const [fill, setFillColor] = useState(null);
  const [dashSize, setDashSize] = useState(0);
  const [gapSize, setGapSize] = useState(0);
  const [opacity, setOpacity] = useState(100);
  const [snap, setSnap] = useState(false);
  const [font, setFont] = useState("");
  const [fontSize, setFontSize] = useState(30);
  const [fontWeight, setFontWeight] = useState("normal");
  const [fontStyle, setFontStyle] = useState("regular");
  const [textHighlight, setHighlight] = useState("yellow");
  //const [shapeCount, setShapeCount] = useState(0);

  var newShape = true;
  var Rectangle = null;
  var ellipse = null;
  var circle = null;
  var line = null;
  var arrow = null;
  var selectedShape = null;

  var startX, startY, origX, origY, deltaX, deltaY, pointer;
  var polyPoints = [];
  var polyLines = [];

  var anchors = [];

  //var lines = [];
  //var lineCounter = 0;
  var drawingObject = {};
  drawingObject.type = "";
  drawingObject.background = "";
  drawingObject.border = "";
  //var pgPoints = [];
  //var polyline = null;

  //var polygon = null;
  var fromx,
    fromy,
    tox,
    toy,
    pline,
    angle,
    headlen = 10;
  //  var arrowPoints = [];

  function Point(x, y) {
    this.x = x;
    this.y = y;
  }

  const onObjectModified = useCallback(
    (e) => {
      const newCanvasState = e.target.canvas.toJSON(["name"]);
      //const newCanvasState = canvas.toJSON(["name"]);
      setCanvasState(newCanvasState);
      // Limit history depth
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
    },
    [setCanvasState, setCanvasHistory]
  );

  React.useEffect(() => {
    const fontAvailable = new Set();
    for (const font of fontCheck.values()) {
      if (document.fonts.check(`12px "${font}"`)) {
        fontAvailable.add(font);
        var x = document.getElementById("fonts");
        var option = document.createElement("option");
        option.text = font;
        x.add(option);
      }
      //console.log("Available Fonts:", [...fontAvailable.values()]);
    }
  }, []);
  React.useEffect(() => {}, [canvasState]);

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.loadFromJSON(canvasState);

    if (canvasTool !== "select") {
      canvas.selection = false;
      canvas.forEachObject(function (o) {
        o.selectable = false;
        o.hoverCursor = "move";
      });
    } else {
      canvas.selection = true;
      canvas.forEachObject(function (o) {
        o.selectable = true;
      });
    }

    //canvas.on("object:added", onObjectModified);
    canvas.on("object:modified", onObjectModified);
    canvas.on("mouse:dblclick", function (e) {
      if (canvasTool === "polygon") {
        canvas.remove(line);
        for (var i = 0; i < polyLines.length; i++) {
          canvas.remove(polyLines[i]);
        }
        newShape = true;
        isDrawing.current = false;
        var polyGon = null;
        polyGon = new fabric.Polygon(polyPoints, {
          stroke: stroke,
          fill: fill ? fill : "black",
          strokeWidth: strokeWidth,
          selectable: false,
          hoverCursor: "default",
          name: "shape-polygon-" + shapeCount.current,
        });
        canvas.add(polyGon);
        editPoly.current = true;
        canvas.renderAll();
        shapeCount.current++;
        const newCanvasState = canvas.toJSON(["name"]);
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }

      if (canvasTool === "polyline") {
        canvas.remove(line);
        for (var i = 0; i < polyLines.length; i++) {
          canvas.remove(polyLines[i]);
        }
        newShape = true;
        isDrawing.current = false;
        var maxX = Math.max(
          polyPoints[0].x,
          polyPoints[polyPoints.length - 1].x
        );
        var minX = Math.min(
          polyPoints[0].x,
          polyPoints[polyPoints.length - 1].x
        );
        var maxY = Math.max(
          polyPoints[0].y,
          polyPoints[polyPoints.length - 1].y
        );
        var minY = Math.min(
          polyPoints[0].y,
          polyPoints[polyPoints.length - 1].y
        );
        var xDiff = maxX - minX;
        var yDiff = maxY - minY;
        var distance = Math.sqrt(xDiff * xDiff + yDiff * yDiff);
        //console.log(distance);
        var polyLine = null;
        if (distance <= 10) {
          polyLine = new fabric.Polyline(polyPoints, {
            stroke: stroke,
            fill: fill ? fill : "black",
            strokeWidth: strokeWidth,
            selectable: false,
            hoverCursor: "default",
            name: "shape-polyline-" + shapeCount.current,
          });
        } else {
          polyLine = new fabric.Polyline(polyPoints, {
            stroke: stroke,
            fill: "",
            strokeWidth: strokeWidth,
            selectable: false,
            hoverCursor: "default",
            name: "shape-polyline-" + shapeCount.current,
          });
        }
