    canvas.on("mouse:wheel", function (opt) {
      var delta = opt.e.deltaY;
      var zoom = canvas.getZoom();
      zoom *= 0.999 ** delta;
      if (zoom > 20) zoom = 20;
      if (zoom < 0.01) zoom = 0.01;
      canvas.zoomToPoint({ x: opt.e.offsetX, y: opt.e.offsetY }, zoom);
      opt.e.preventDefault();
      opt.e.stopPropagation();
      var vpt = this.viewportTransform;
      if (zoom < 400 / 1000) {
        vpt[4] = 200 - (1000 * zoom) / 2;
        vpt[5] = 200 - (1000 * zoom) / 2;
      } else {
        if (vpt[4] >= 0) {
          vpt[4] = 0;
        } else if (vpt[4] < canvas.getWidth() - 1000 * zoom) {
          vpt[4] = canvas.getWidth() - 1000 * zoom;
        }
        if (vpt[5] >= 0) {
          vpt[5] = 0;
        } else if (vpt[5] < canvas.getHeight() - 1000 * zoom) {
          vpt[5] = canvas.getHeight() - 1000 * zoom;
        }
      }
    });
    canvas.on("mouse:down", function (opt) {
      if (canvasTool !== "select") {
        this.selection = false;
      }
      var evt = opt.e;
      if (evt.altKey === true) {
        this.isDragging = true;
        this.selection = false;
        this.lastPosX = evt.clientX;
        this.lastPosY = evt.clientY;
      }
    });
    canvas.on("mouse:move", function (opt) {
      if (this.isDragging) {
        var e = opt.e;
        var vpt = this.viewportTransform;
        vpt[4] += e.clientX - this.lastPosX;
        vpt[5] += e.clientY - this.lastPosY;
        this.requestRenderAll();
        this.lastPosX = e.clientX;
        this.lastPosY = e.clientY;
      }
      if (newShape === true && canvasTool !== "pen") {
        //console.log("no moving");
        return;
      }
      //canvas.renderAll();
      if (canvasTool === "rect") {
        var pointer = canvas.getPointer(opt);
        if (origX > pointer.x) {
          Rectangle.set({ left: Math.abs(pointer.x) });
        }
        if (origY > pointer.y) {
          Rectangle.set({ top: Math.abs(pointer.y) });
        }
        Rectangle.set({ width: Math.abs(origX - pointer.x) });
        Rectangle.set({ height: Math.abs(origY - pointer.y) });

        canvas.renderAll();
      }
      if (canvasTool === "line") {
        pointer = canvas.getPointer(opt);
        line.set({ x2: pointer.x, y2: pointer.y });
        canvas.renderAll();
      }
      if (canvasTool === "arrow") {
        pointer = canvas.getPointer(opt);
        line.set({
          x2: pointer.x,
          y2: pointer.y,
        });

        let dx = line.x2 - line.x1,
          dy = line.y2 - line.y1;

        let angle = Math.atan2(dy, dx);
        angle *= 180 / Math.PI;
        angle += 90;

        arrow.set({
          top: line.y2,
          left: line.x2,
          angle: angle,
          width: 15,
          height: 15,
        });
        canvas.renderAll();
      }
      if (canvasTool === "polyline") {
        if (isDrawing.current) {
          // var startPoint = polyPoints[polyPoints.length - 1];
          var endPoint = canvas.getPointer(opt);
          line.set({
            x2: endPoint.x,
            y2: endPoint.y,
          });
          canvas.renderAll();
        }
      }
      if (canvasTool === "polygon") {
        if (isDrawing.current) {
          // var startPoint = polyPoints[polyPoints.length - 1];
          var endPoint = canvas.getPointer(opt);
          line.set({
            x2: endPoint.x,
            y2: endPoint.y,
          });
          canvas.renderAll();
        }
      }
      if (canvasTool === "arc") {
        pointer = canvas.getPointer(opt);
        var radius =
          Math.max(Math.abs(origY - pointer.y), Math.abs(origX - pointer.x)) /
          2;
        if (radius > circle.strokeWidth) {
          radius -= circle.strokeWidth / 2;
        }
        circle.set({ radius: radius });

        if (origX > pointer.x) {
          circle.set({ originX: "right" });
          circle.set({ angle: -135 });
        } else {
          circle.set({ originX: "left" });
          circle.set({ angle: 135 });
        }
        if (origY > pointer.y) {
          circle.set({ originY: "bottom" });
          //circle.set({ angle: 0 });
          //circle.set({ angle: 45 });
        } else {
          circle.set({ originY: "top" });
          //circle.set({ angle: 0 });
          //circle.set({ angle: 135 });
        }
        // Render the Canvas
        canvas.renderAll();
      }

      if (canvasTool === "circle") {
        pointer = canvas.getPointer(opt);
        var radius =
          Math.max(Math.abs(origY - pointer.y), Math.abs(origX - pointer.x)) /
          2;
        if (radius > circle.strokeWidth) {
          radius -= circle.strokeWidth / 2;
        }
        circle.set({ radius: radius });

        if (origX > pointer.x) {
          circle.set({ originX: "right" });
        } else {
          circle.set({ originX: "left" });
        }
        if (origY > pointer.y) {
          circle.set({ originY: "bottom" });
        } else {
          circle.set({ originY: "top" });
        }
        // Render the Canvas
        canvas.renderAll();
      }
      if (canvasTool === "ellipse") {
        pointer = canvas.getPointer(opt);
        var rx = Math.abs(origX - pointer.x) / 2;
        var ry = Math.abs(origY - pointer.y) / 2;
        if (rx > ellipse.strokeWidth) {
          rx -= ellipse.strokeWidth / 2;
        }
        if (ry > ellipse.strokeWidth) {
          ry -= ellipse.strokeWidth / 2;
        }
        ellipse.set({ rx: rx, ry: ry });

        if (origX > pointer.x) {
          ellipse.set({ originX: "right" });
        } else {
          ellipse.set({ originX: "left" });
        }
        if (origY > pointer.y) {
          ellipse.set({ originY: "bottom" });
        } else {
          ellipse.set({ originY: "top" });
        }
        canvas.renderAll();
      }
    });
    canvas.on("mouse:over", function (e) {
      if (e.target) {
        currentStroke.current = e.target.get("stroke");
        e.target.set("stroke", "red");
        canvas.renderAll();
      }
    });
    canvas.on("mouse:out", function (e) {
      if (e.target) {
        e.target.set("stroke", currentStroke.current);
        canvas.renderAll();
      }
    });

    setCanvas(canvas);
    return () => {
      canvas.dispose();
    };
  }, [canvasRef, canvasTool, onObjectModified, setCanvas]);

  const moveHistory = useCallback(
    (step) => {
      const currentStateIndex = canvasHistory.indexOf(canvasState);
      const prevState = canvasHistory[currentStateIndex + step];
      canvas.loadFromJSON(prevState);
      setCanvasState(prevState);
    },
    [canvas, canvasState, canvasHistory, setCanvasState]
  );

  const onUndo = useCallback(() => moveHistory(-1), [moveHistory]);

  const onRedo = useCallback(() => moveHistory(1), [moveHistory]);

