import React, { useRef, useState, useCallback } from "react";
import "./styles.css";
import styles from "./rightMenu.module.css";
import tbStyles from "./toolbar.module.css";
import { fabric } from "fabric";

const initialState = {
  version: "3.6.3",
  objects: [
    {
      type: "rect",
      version: "3.6.3",
      originX: "left",
      originY: "top",
      left: 106,
      top: 33,
      width: 60,
      height: 70,
      fill: "red",
      stroke: null,
      strokeWidth: 1,
      strokeDashArray: null,
      strokeLineCap: "butt",
      strokeDashOffset: 0,
      strokeLineJoin: "miter",
      strokeMiterLimit: 4,
      scaleX: 1,
      scaleY: 1,
      angle: 0,
      flipX: false,
      flipY: false,
      opacity: 1,
      shadow: null,
      visible: true,
      clipTo: null,
      backgroundColor: "",
      fillRule: "nonzero",
      paintFirst: "fill",
      globalCompositeOperation: "source-over",
      transformMatrix: null,
      skewX: 0,
      skewY: 0,
      rx: 0,
      ry: 0,
    },
  ],
};

// Or you can use:
// const fabric = require("fabric").fabric;
//const canvas = new fabric.Canvas("my-fabric-canvas");
const App = () => {
  const canvasRef = useRef();
  const [canvas, setCanvas] = useState();
  const [canvasHistory, setCanvasHistory] = useState([initialState]);
  const [canvasState, setCanvasState] = useState(initialState);
  const [canvasTool, setCanvasTool] = useState("select");

  var nRect = true;
  var Rectangle = null;
  var origX, origY;

  const onObjectModified = useCallback(
    (e) => {
      const newCanvasState = e.target.canvas.toJSON();
      setCanvasState(newCanvasState);
      // Limit history depth
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
    },
    [setCanvasState, setCanvasHistory]
  );

  React.useEffect(() => {
    //console.log(JSON.stringify(canvasState));
  }, [canvasState]);

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.loadFromJSON(initialState);
    //canvas.on("object:added", onObjectModified);
    canvas.on("object:modified", onObjectModified);

    //canvas.on("object:removed", onObjectModified);

    const rect = new fabric.Rect({
      width: 50,
      height: 50,
      fill: "blue",
      angle: 10,
      top: 20,
      left: 20,
    });
    const textbox = new fabric.Textbox("Click on the Rectangle to move it.", {
      fontSize: 20,
      left: 50,
      top: 100,
      width: 200,
    });
    canvas.add(textbox);
    canvas.add(rect);
    setCanvas(canvas);
    //refCanvas.current = canvas;
    // UseEffect's cleanup function
    return () => {
      canvas.dispose();
      //setCanvas(null);
    };
  }, [canvasRef, onObjectModified, setCanvas]);

  const moveHistory = useCallback(
    (step) => {
      const currentStateIndex = canvasHistory.indexOf(canvasState);
      const prevState = canvasHistory[currentStateIndex + step];
      canvas.loadFromJSON(prevState);
      setCanvasState(prevState);
    },
    [canvas, canvasState, canvasHistory, setCanvasState]
  );

  const onUndo = useCallback(() => moveHistory(-1), [moveHistory]);

  const onRedo = useCallback(() => moveHistory(1), [moveHistory]);

  const handleMouseClick = (event) => {
    //let o = canvas.getActiveObject();
    //console.log(o);
    if (canvasTool === "select") {
    }
    if (canvasTool === "line") {
    }
    if (canvasTool === "rect") {
      if (nRect) {
        var pointer = canvas.getPointer(event);
        origX = pointer.x;
        origY = pointer.y;
        Rectangle = new fabric.Rect({
          left: origX,
          top: origY,
          fill: "transparent",
          stroke: "red",
          strokeWidth: 3,
        });
        //console.log(Rectangle);
        canvas.add(Rectangle);
        setCanvas(canvas);
        //canvas.renderAll();
        //setRect((prevState) => (prevState = false));
        nRect = false;
        //setNewRectangle(false);
      } else {
        pointer = canvas.getPointer(event);
        if (origX > pointer.x) {
          Rectangle.set({ left: Math.abs(pointer.x) });
        }
        if (origY > pointer.y) {
          Rectangle.set({ top: Math.abs(pointer.y) });
        }
        Rectangle.set({ width: Math.abs(origX - pointer.x) });
        Rectangle.set({ height: Math.abs(origY - pointer.y) });
        canvas.setActiveObject(Rectangle);
        //setNewRectangle(true);
        nRect = true;
        // Render the Canvas
        canvas.renderAll();

        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
  };
  const handleMouseMove = (event) => {
    if (nRect) return;
    //canvas.renderAll();
    var pointer = canvas.getPointer(event);
    if (origX > pointer.x) {
      Rectangle.set({ left: Math.abs(pointer.x) });
    }
    if (origY > pointer.y) {
      Rectangle.set({ top: Math.abs(pointer.y) });
    }
    Rectangle.set({ width: Math.abs(origX - pointer.x) });
    Rectangle.set({ height: Math.abs(origY - pointer.y) });

    //console.log(Rectangle);
    canvas.renderAll();

    //var pointer = canvas.getPointer(event);
    //console.log(pointer);
  };
  let url = "#";
  return (
    <div
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
      className="App"
    >
      <div className={tbStyles.propBar}>
        <a href={url}>
          <button className={tbStyles.buttontoolbar} onclick={onUndo} id="undo">
            <div className={styles.tooltipDown}>
              <img src="undo2.png" alt="Undo"></img>
              <span className={styles.tooltiptextDown}>Undo</span>
            </div>
          </button>
          <button className={styles.buttontoolbar} onclick={onRedo} id="redo">
            <div className={styles.tooltipDown}>
              <img src="redo2.png" alt="Redo"></img>
              <span className={styles.tooltiptextDown}>Redo</span>
            </div>
          </button>
          <button className={styles.buttontoolbar} onclick={onRedo} id="del">
            <div className={styles.tooltipDown}>
              <img src="delete2.png" alt="Delete"></img>
              <span className={styles.tooltiptextDown}>Delete</span>
            </div>
          </button>
        </a>
      </div>
      <div>
        <h1>Hello CodeSandbox</h1>
        <h2>Start editing to see some magic happen!</h2>
        <button onClick={onUndo} disabled={canvasHistory[0] === canvasState}>
          Undo
        </button>
        <button
          onClick={onRedo}
          disabled={canvasHistory[canvasHistory.length - 1] === canvasState}
        >
          Redo
        </button>
      </div>
      <div className={styles.right}>
        <div className={styles.tab}>
          <button
            onClick={(e) => {
              setCanvasTool("select");
            }}
            id="defaultSelect"
          >
            <div className={styles.tooltip}>
              <img src={"images/Select2.png"} alt="Select"></img>
              <span className={styles.tooltiptext}>Selection</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("text");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/TextBox2.png"} alt="TextBox"></img>
              <span className={styles.tooltiptext}>Text</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("pen");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/PenTool2.png"} alt="Pen"></img>
              <span className={styles.tooltiptext}>Free-hand</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("cloud");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Cloud2.png"} alt="Cloud"></img>
              <span className={styles.tooltiptext}>Cloud</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("callout");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/CallOut2.png"} alt="Callout"></img>
              <span className={styles.tooltiptext}>Callout</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("image");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Image2.png"} alt="Image Edit"></img>
              <span className={styles.tooltiptext}>Image Edit</span>
            </div>
          </button>
          <div className={styles.tabspace}></div>
          <button
            onClick={(e) => {
              setCanvasTool("line");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Line2.png"} alt="Line"></img>
              <span className={styles.tooltiptext}>Line</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("polyline");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Polyline2.png"} alt="Polyline"></img>
              <span className={styles.tooltiptext}>Polyline</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("arrow");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Arrow2.png"} alt="Arrow"></img>
              <span className={styles.tooltiptext}>Arrow</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("arc");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Arc2.png"} alt="Arc"></img>
              <span className={styles.tooltiptext}>Arc</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("rect");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Rectangle2.png"} alt="Rectangle"></img>
              <span className={styles.tooltiptext}>Rectangle</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("polygon");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Polygon2.png"} alt="Polygon"></img>
              <span className={styles.tooltiptext}>Polygon</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("rpolygon");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/RPolygon2.png"} alt="Regular Polygon"></img>
              <span className={styles.tooltiptext}>Regular Polygon</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("circle");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Circle2.png"} alt="Circle"></img>
              <span className={styles.tooltiptext}>Circle</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("ellipse");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Ellipse2.png"} alt="Ellipse"></img>
              <span className={styles.tooltiptext}>Ellipse</span>
            </div>
          </button>
          <div className={styles.tabspace}></div>
          <button
            onClick={(e) => {
              setCanvasTool("dimension");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Dimension2.png"} alt="Dimension"></img>
              <span className={styles.tooltiptext}>Dimension</span>
            </div>
          </button>

          <button
            onClick={(e) => {
              setCanvasTool("area");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Area2.png"} alt="Area"></img>
              <span className={styles.tooltiptext}>Area</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("length");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Length2.png"} alt="Length"></img>
              <span className={styles.tooltiptext}>Length</span>
            </div>
          </button>
          <button
            onClick={(e) => {
              setCanvasTool("perimeter");
            }}
          >
            <div className={styles.tooltip}>
              <img src={"images/Perimeter2.png"} alt="Perimeter"></img>
              <span className={styles.tooltiptext}>Perimeter</span>
            </div>
          </button>
        </div>
      </div>
      <canvas
        ref={canvasRef}
        id="my-fabric-canvas"
        width="1920"
        height="1080"
      />
    </div>
  );
};

export default App;
