      const newCanvasState = e.target.canvas.toJSON();
      setCanvasState(newCanvasState);
      // Limit history depth
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
    },
    [setCanvasState, setCanvasHistory]
  );

  React.useEffect(() => {
    //console.log(JSON.stringify(canvasState));
  }, [canvasState]);

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.loadFromJSON(initialState);
    //canvas.on("object:added", onObjectModified);
    canvas.on("object:modified", onObjectModified);

    //canvas.on("object:removed", onObjectModified);

    const rect = new fabric.Rect({
      width: 50,
      height: 50,
      fill: "blue",
      angle: 10,
      top: 20,
      left: 20,
    });
    const textbox = new fabric.Textbox("Click on the Rectangle to move it.", {
      fontSize: 20,
      left: 50,
      top: 100,
      width: 200,
    });
    canvas.add(textbox);
    canvas.add(rect);
    setCanvas(canvas);
    //refCanvas.current = canvas;
    // UseEffect's cleanup function
    return () => {
      canvas.dispose();
      //setCanvas(null);
    };
  }, [canvasRef, onObjectModified, setCanvas]);

  const moveHistory = useCallback(
    (step) => {
      const currentStateIndex = canvasHistory.indexOf(canvasState);
      const prevState = canvasHistory[currentStateIndex + step];
      canvas.loadFromJSON(prevState);
      setCanvasState(prevState);
    },
    [canvas, canvasState, canvasHistory, setCanvasState]
  );

  const onUndo = useCallback(() => moveHistory(-1), [moveHistory]);

  const onRedo = useCallback(() => moveHistory(1), [moveHistory]);

  const handleMouseClick = (event) => {
    //let o = canvas.getActiveObject();
    //console.log(o);
    if (canvasTool === "select") {
    }
    if (canvasTool === "line") {
    }
    if (canvasTool === "rect") {
      if (nRect) {
        var pointer = canvas.getPointer(event);
        origX = pointer.x;
        origY = pointer.y;
        Rectangle = new fabric.Rect({
          left: origX,
          top: origY,
          fill: fill,
          stroke: stroke,
          strokeWidth: strokeWidth,
          strokeDashArray: [dashSize, gapSize],
          opacity: opacity,
        });
        //console.log(Rectangle);
        canvas.add(Rectangle);
        setCanvas(canvas);
        //canvas.renderAll();
        //setRect((prevState) => (prevState = false));
        nRect = false;
        //setNewRectangle(false);
      } else {
        pointer = canvas.getPointer(event);
        if (origX > pointer.x) {
          Rectangle.set({ left: Math.abs(pointer.x) });
        }
        if (origY > pointer.y) {
          Rectangle.set({ top: Math.abs(pointer.y) });
        }
        Rectangle.set({ width: Math.abs(origX - pointer.x) });
        Rectangle.set({ height: Math.abs(origY - pointer.y) });
        canvas.setActiveObject(Rectangle);
        //setNewRectangle(true);
        nRect = true;
        // Render the Canvas
        canvas.renderAll();

        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
  };
  const handleMouseMove = (event) => {
    if (nRect) return;
    //canvas.renderAll();
    var pointer = canvas.getPointer(event);
    if (origX > pointer.x) {
      Rectangle.set({ left: Math.abs(pointer.x) });
    }
    if (origY > pointer.y) {
      Rectangle.set({ top: Math.abs(pointer.y) });
    }
    Rectangle.set({ width: Math.abs(origX - pointer.x) });
    Rectangle.set({ height: Math.abs(origY - pointer.y) });

    //console.log(Rectangle);
    canvas.renderAll();

    //var pointer = canvas.getPointer(event);
    //console.log(pointer);
  };

  const setShapeAttributes = (attributes) => {
    var activeShape = canvas.getActiveObject();
    if (activeShape) {
      var attributes = attributes || {};
      activeShape.set({
        strokeWidth: attributes.strokeWidth || activeShape.strokeWidth,
        stroke:
          attributes.stroke === "nill"
            ? null
            : attributes.stroke || activeShape.stroke,
        fill: attributes.fill || activeShape.fill,
        opacity: attributes.opacity / 100 || activeShape.opacity,
        strokeDashArray:
          attributes.strokeDashArray || activeShape.strokeDashArray,
