import React, { useRef, useState } from "react";
import { fabric } from "fabric";
import { SketchPicker } from "react-color";
import MediaQuery from "react-responsive";
import reactCSS from "reactcss";

import "./styles.css";
import styles from "./rightMenu.module.css";
import tbStyles from "./toolbar.module.css";

const initialState = {
  version: "3.6.3",
  objects: [
    {
      type: "rect",
      version: "3.6.3",
      originX: "left",
      originY: "top",
      left: 0,
      top: 0,
      width: 1,
      height: 1,
      fill: "black",
      stroke: null,
      strokeWidth: 1,
      strokeDashArray: null,
      strokeLineCap: "butt",
      strokeDashOffset: 0,
      strokeLineJoin: "miter",
      strokeMiterLimit: 4,
      scaleX: 1,
      scaleY: 1,
      angle: 0,
      flipX: false,
      flipY: false,
      opacity: 1,
      shadow: null,
      visible: false,
      clipTo: null,
      backgroundColor: "",
      fillRule: "nonzero",
      paintFirst: "fill",
      globalCompositeOperation: "source-over",
      transformMatrix: null,
      skewX: 0,
      skewY: 0,
      rx: 0,
      ry: 0,
      name: "shape-rect-n",
    },
  ],
};

const fontCheck = new Set(
  [
    // Windows 10
    "Arial",
    "Arial Black",
    "Bahnschrift",
    "Calibri",
    "Cambria",
    "Cambria Math",
    "Candara",
    "Comic Sans MS",
    "Consolas",
    "Constantia",
    "Corbel",
    "Courier New",
    "Ebrima",
    "Franklin Gothic Medium",
    "Gabriola",
    "Gadugi",
    "Georgia",
    "HoloLens MDL2 Assets",
    "Impact",
    "Ink Free",
    "Javanese Text",
    "Leelawadee UI",
    "Lucida Console",
    "Lucida Sans Unicode",
    "Malgun Gothic",
    "Marlett",
    "Microsoft Himalaya",
    "Microsoft JhengHei",
    "Microsoft New Tai Lue",
    "Microsoft PhagsPa",
    "Microsoft Sans Serif",
    "Microsoft Tai Le",
    "Microsoft YaHei",
    "Microsoft Yi Baiti",
    "MingLiU-ExtB",
    "Mongolian Baiti",
    "MS Gothic",
    "MV Boli",
    "Myanmar Text",
    "Nirmala UI",
    "Palatino Linotype",
    "Segoe MDL2 Assets",
    "Segoe Print",
    "Segoe Script",
    "Segoe UI",
    "Segoe UI Historic",
    "Segoe UI Emoji",
    "Segoe UI Symbol",
    "SimSun",
    "Sitka",
    "Sylfaen",
    "Symbol",
    "Tahoma",
    "Times New Roman",
    "Trebuchet MS",
    "Verdana",
    "Webdings",
    "Wingdings",
    "Yu Gothic",
    // macOS
    "American Typewriter",
    "Andale Mono",
    "Arial",
    "Arial Black",
    "Arial Narrow",
    "Arial Rounded MT Bold",
    "Arial Unicode MS",
    "Avenir",
    "Avenir Next",
    "Avenir Next Condensed",
    "Baskerville",
    "Big Caslon",
    "Bodoni 72",
    "Bodoni 72 Oldstyle",
    "Bodoni 72 Smallcaps",
    "Bradley Hand",
    "Brush Script MT",
    "Chalkboard",
    "Chalkboard SE",
    "Chalkduster",
    "Charter",
    "Cochin",
    "Comic Sans MS",
    "Copperplate",
    "Courier",
    "Courier New",
    "Didot",
    "DIN Alternate",
    "DIN Condensed",
    "Futura",
    "Geneva",
    "Georgia",
    "Gill Sans",
    "Helvetica",
    "Helvetica Neue",
    "Herculanum",
    "Hoefler Text",
    "Impact",
    "Lucida Grande",
    "Luminari",
    "Marker Felt",
    "Menlo",
    "Microsoft Sans Serif",
    "Monaco",
    "Noteworthy",
    "Optima",
    "Palatino",
    "Papyrus",
    "Phosphate",
    "Rockwell",
    "Savoye LET",
    "SignPainter",
    "Skia",
    "Snell Roundhand",
    "Tahoma",
    "Times",
    "Times New Roman",
    "Trattatello",
    "Trebuchet MS",
    "Verdana",
    "Zapfino",
  ].sort()
);
// Or you can use:
// const fabric = require("fabric").fabric;
//const canvas = new fabric.Canvas("my-fabric-canvas");
const App = () => {
  // for undo/redoing
  const [past, setPast] = useState([initialState]);
  const [future, setFuture] = useState([initialState]);
  //const [newAction, setNewAction] = useState(0);
  const [pageLoad, setPageLoad] = useState(null);
  const canvasRef = useRef();
  const mainDiv = useRef(null);
  const rightBar = useRef();
  const shapeCount = useRef(0);
  const isDrawing = useRef(false);
  const currentStroke = useRef("");
  const editPoly = useRef(true);
  const openPoly = useRef(null);
  const polyType = useRef("");
  //anchors
  const [canvas, setCanvas] = useState();
  //const [canvasHistory, setCanvasHistory] = useState([initialState]);
  //const [canvasHistory, setCanvasHistory] = useState([]);
  // const [canvasState, setCanvasState] = useState(initialState);
  //const [canvasState, setCanvasState] = useState();
  const [canvasTool, setCanvasTool] = useState("select");
  const [strokeWidth, setStrokeWidth] = useState(1);
  // const [stroke, setStrokeColor] = useState("black");
  const [stroke, setStrokeColor] = useState({
    r: "0",
    g: "0",
    b: "0",
    a: "1",
  });
  const [fill, setFillColor] = useState({
    r: "0",
    g: "0",
    b: "0",
    a: "0",
  });
  const [textHighlight, setHighlight] = useState({
    r: "255",
    g: "255",
    b: "0",
    a: "1",
  });
  // const [fill, setFillColor] = useState("black");
  const [dashSize, setDashSize] = useState(0);
  const [gapSize, setGapSize] = useState(0);
  const [opacity, setOpacity] = useState(100);
  // const [snap, setSnap] = useState(false);
  const [font, setFont] = useState("");
  const [fontSize, setFontSize] = useState(30);
  const [fontWeight, setFontWeight] = useState("normal");
  const [fontStyle, setFontStyle] = useState("regular");
  //const [textHighlight, setHighlight] = useState("yellow");

  const [showStrokePicker, setShowStrokePicker] = useState(false);
  const [showFillPicker, setShowFillPicker] = useState(false);
  const [showHighlightPicker, setShowHighlightPicker] = useState(false);
  //const [shapeCount, setShapeCount] = useState(0);

  //const opacity = useRef(100);
  const snap = useRef(false);
  const newShape = useRef(true);
  const anchors = useRef([]);
  const pointer = useRef();
  const line = useRef(null);
  const origX = useRef();
  const origY = useRef();
  const angle = useRef();
  const arrow = useRef(null);
  const selectedShape = useRef(null);
  const polyPoints = useRef([]);
  const rpolygon = useRef(null);
  const Rectangle = useRef(null);
  const circle = useRef(null);
  const ellipse = useRef(null);
  const polyLines = useRef([]);
  const currentToolId = useRef(null);

  var currentOpenPanel = "none";
  var drawingObject = {};
  drawingObject.type = "";
  drawingObject.background = "";
  drawingObject.border = "";

  const StrokeSwatchClick = () => {
    setShowStrokePicker((showStrokePicker) => !showStrokePicker);
    if (!showStrokePicker) {
      //Todo
      var activeShape = canvas.getActiveObject();
      if (activeShape) {
        if (activeShape.stroke === "rgba(255,255,255,0)") {
          setStrokeColor({ r: 255, g: 255, b: 255, a: 1 });
          setShapeAttributes({
            stroke: "rgba(255,255,255,1)",
          });
        }
      }
    }
  };
  const FillSwatchClick = () => {
    setShowFillPicker((showFillPicker) => !showFillPicker);
    if (!showFillPicker) {
      //Todo
      var activeShape = canvas.getActiveObject();
      if (activeShape) {
        if (activeShape.fill === "rgba(255,255,255,0)") {
          setFillColor({ r: 255, g: 255, b: 255, a: 1 });
          setShapeAttributes({
            fill: "rgba(255,255,255,1)",
          });
        }
      }
    }
  };
  const HighlightSwatchClick = () => {
    setShowHighlightPicker((showHighlightPicker) => !showHighlightPicker);
  };
  const StrokeSwatchClose = () => {
    setShowStrokePicker(false);
  };
  const FillSwatchClose = () => {
    setShowFillPicker(false);
  };
  const HighlightSwatchClose = () => {
    setShowHighlightPicker(false);
  };
  const StrokeSwatchChange = (color) => {
    //console.log(stroke);
    setStrokeColor(color.rgb);
    setShapeAttributes({
      stroke:
        "rgba(" +
        color.rgb.r +
        ", " +
        color.rgb.g +
        ", " +
        color.rgb.b +
        ", " +
        color.rgb.a +
        ")",
    });
  };
  const FillSwatchChange = (color) => {
    //console.log(stroke);
    setFillColor(color.rgb);
    setShapeAttributes({
      fill:
        "rgba(" +
        color.rgb.r +
        ", " +
        color.rgb.g +
        ", " +
        color.rgb.b +
        ", " +
        color.rgb.a +
        ")",
    });
    //console.log(swatchStyles.fill);
  };
  const HighlightSwatchChange = (color) => {
    //console.log(stroke);
    setHighlight(color.rgb);
    setShapeAttributes({
      textBackgroundColor:
        "rgba(" +
        color.rgb.r +
        ", " +
        color.rgb.g +
        ", " +
        color.rgb.b +
        ", " +
        color.rgb.a +
        ")",
    });
  };
  var calcPolygonPoints = (sideCount, radius) => {
    var sweep = (Math.PI * 2) / sideCount;
    var cx = radius;
    var cy = radius;
    var points = [];

    for (var i = 0; i < sideCount; i++) {
      var x = cx + radius * Math.cos(i * sweep);
      var y = cy + radius * Math.sin(i * sweep);
      points.push({ x: x, y: y });
    }
    return points;
  };
  /*
  function click(id) {
    var ev = new MouseEvent("click", {
      view: window,
      bubbles: true,
      cancelable: true,
    });

    var el = document.getElementById(id);
    el.dispatchEvent(ev);
  }
  */
  const saveCanvasState = (newCanvasState) => {
    past.push(newCanvasState);
    setPast(past);
    setFuture([]);
    sessionStorage.clear();
    sessionStorage.myFabricJSCanvas = JSON.stringify(newCanvasState);
    //console.log(sessionStorage.myFabricJSCanvas);
  };
  /*
  const onObjectModified = useCallback(
    (e) => {
      const newCanvasState = e.target.canvas.toJSON(["name"]);
      //const newCanvasState = canvas.toJSON(["name"]);
      setCanvasState(newCanvasState);
      // Limit history depth
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      //setPast([...past, newCanvasState]);
      //setFuture([]);
      console.log(newCanvasState);
    },
    [setCanvasState, setCanvasHistory]
  );
*/
  React.useEffect(() => {
    //document.getElementById("select").click();
    const fontAvailable = new Set();
    for (const font of fontCheck.values()) {
      if (document.fonts.check(`12px "${font}"`)) {
        fontAvailable.add(font);
        var x = document.getElementById("fonts");
        var option = document.createElement("option");
        option.text = font;
        x.add(option);
      }
    }
  }, []);
  // React.useEffect(() => {}, [canvasState]);

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.set({
      fireMiddleClick: true, // <-- enable firing of middle click events
      stopContextMenu: true,
    });
    //canvas.loadFromJSON(canvasState);
    //    console.log(past[past.length - 1]);
    if (pageLoad === null && sessionStorage.myFabricJSCanvas !== undefined) {
      setPageLoad(1);
      //console.log(sessionStorage.myFabricJSCanvas);
      const data = JSON.parse(sessionStorage.myFabricJSCanvas);
      canvas.loadFromJSON(data);
      canvas.forEachObject(function (o) {
        if (o.name.includes("anchor")) {
          canvas.remove(o);
        }
      });
      anchors.current = [];
      var json = canvas.toJSON(["name"]);
      past.push(json);
      setPast(past);
      setFuture([]);
    } else {
      canvas.loadFromJSON(past[past.length - 1]);
    }
    canvas.renderAll();
    editPoly.current = true;
    if (canvasTool === "pen") {
      canvas.isDrawingMode = 1;
      canvas.freeDrawingBrush.color =
        "rgba(" +
        stroke.r +
        ", " +
        stroke.g +
        ", " +
        stroke.b +
        ", " +
        stroke.a +
        ")";
      canvas.freeDrawingBrush.width = strokeWidth;
    }
    if (canvasTool !== "select") {
      canvas.selection = false;
      canvas.defaultCursor = "crosshair";
      canvas.forEachObject(function (o) {
        o.selectable = false;
        o.hoverCursor = "move";
      });
      if (openPoly.current !== null) {
        openPoly.current = null;
        canvas.forEachObject(function (o) {
          if (o.name.includes("anchor")) {
            canvas.remove(o);
          }
        });
        anchors.current = [];
        canvas.renderAll();
        const newCanvasState = canvas.toJSON(["name"]);
        saveCanvasState(newCanvasState);
        sessionStorage.myFabricJSCanvas = JSON.stringify(newCanvasState);
      }
    } else {
      canvas.selection = true;
      canvas.forEachObject(function (o) {
        o.selectable = true;
      });
    }

    canvas.on("object:rotating", function (options) {
      if (!snap.current) return;
      var angle = options.target.angle;
      var locked = Math.ceil(options.target.angle / 5) * 5;
      if (Math.abs(angle - locked) < 10 || Math.abs(locked - angle) < 10) {
        angle = locked;
      }

      options.target.set({
        angle: angle,
      });
    });
    //canvas.on("object:added", onObjectModified);
    // canvas.on("object:modified", onObjectModified);
    canvas.on("object:modified", function (e) {
      const newCanvasState = e.target.canvas.toJSON(["name"]);
      past.push(newCanvasState);
      setPast(past);
      // console.log(past);
      setFuture([]);
      sessionStorage.clear();
      sessionStorage.myFabricJSCanvas = JSON.stringify(newCanvasState);
    });

    canvas.on("mouse:dblclick", function (e) {
      if (canvasTool === "polygon") {
        canvas.remove(line.current);
        for (var i = 0; i < polyLines.current.length; i++) {
          canvas.remove(polyLines.current[i]);
        }
        newShape.current = true;
        isDrawing.current = false;
        var polyGon = null;
        polyGon = new fabric.Polygon(polyPoints.current, {
          stroke:
            "rgba(" +
            stroke.r +
            ", " +
            stroke.g +
            ", " +
            stroke.b +
            ", " +
            stroke.a +
            ")",
          fill:
            "rgba(" +
            fill.r +
            ", " +
            fill.g +
            ", " +
            fill.b +
            ", " +
            fill.a +
            ")",
          strokeWidth: strokeWidth,
          selectable: false,
          hoverCursor: "default",
          name: "shape-polygon-" + shapeCount.current,
        });
        canvas.add(polyGon);
        editPoly.current = true;
        canvas.setActiveObject(polyGon);
        canvas.renderAll();
        shapeCount.current++;
        const newCanvasState = canvas.toJSON(["name"]);
        // setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        // setCanvasHistory ((history) => [...history, newCanvasState].slice(-32));
        saveCanvasState(newCanvasState);
      }

      if (canvasTool === "polyline") {
        canvas.remove(line.current);
        for (i = 0; i < polyLines.current.length; i++) {
          canvas.remove(polyLines.current[i]);
        }
        newShape.current = true;
        isDrawing.current = false;
        var maxX = Math.max(
          polyPoints.current[0].x,
          polyPoints.current[polyPoints.current.length - 1].x
        );
        var minX = Math.min(
          polyPoints.current[0].x,
          polyPoints.current[polyPoints.current.length - 1].x
        );
        var maxY = Math.max(
          polyPoints.current[0].y,
          polyPoints.current[polyPoints.current.length - 1].y
        );
        var minY = Math.min(
          polyPoints.current[0].y,
