  function handleKeyDown(event) {
    if (event.key === "s") {
      setActiveTool(event, "select");
      return;
    }
    if (event.key === "Delete") {
      deleteObjects();
      return;
    }
    if (event.ctrlKey && event.key === "z") {
      if (past.length === 1) return;
      onUndo();
      return;
    }
    if (event.ctrlKey && event.key === "y") {
      if (future.length === 0) return;
      onRedo();
      return;
