import "rc-color-picker/assets/index.css";
import React, { useRef, useState, useCallback } from "react";
////import { PopoverPicker } from "./PopoverPicker";

import { fabric } from "fabric";
import "./styles.css";
import styles from "./rightMenu.module.css";
import tbStyles from "./toolbar.module.css";
//import { RgbaColorPicker } from "react-colorful";
import ColorPicker from "rc-color-picker";
//import "react-colorful/dist/index.css";

const initialState = {
  version: "3.6.3",
  objects: [
    {
      type: "rect",
      version: "3.6.3",
      originX: "left",
      originY: "top",
      left: 0,
      top: 0,
      width: 1,
      height: 1,
      fill: "red",
      stroke: null,
      strokeWidth: 1,
      strokeDashArray: null,
      strokeLineCap: "butt",
      strokeDashOffset: 0,
      strokeLineJoin: "miter",
      strokeMiterLimit: 4,
      scaleX: 1,
      scaleY: 1,
      angle: 0,
      flipX: false,
      flipY: false,
      opacity: 1,
      shadow: null,
      visible: false,
      clipTo: null,
      backgroundColor: "",
      fillRule: "nonzero",
      paintFirst: "fill",
      globalCompositeOperation: "source-over",
      transformMatrix: null,
      skewX: 0,
      skewY: 0,
      rx: 0,
      ry: 0,
    },
  ],
};

const fontCheck = new Set(
  [
    // Windows 10
    "Arial",
    "Arial Black",
    "Bahnschrift",
    "Calibri",
    "Cambria",
    "Cambria Math",
    "Candara",
    "Comic Sans MS",
    "Consolas",
    "Constantia",
    "Corbel",
    "Courier New",
    "Ebrima",
    "Franklin Gothic Medium",
    "Gabriola",
    "Gadugi",
    "Georgia",
    "HoloLens MDL2 Assets",
    "Impact",
    "Ink Free",
    "Javanese Text",
    "Leelawadee UI",
    "Lucida Console",
    "Lucida Sans Unicode",
    "Malgun Gothic",
    "Marlett",
    "Microsoft Himalaya",
    "Microsoft JhengHei",
    "Microsoft New Tai Lue",
    "Microsoft PhagsPa",
    "Microsoft Sans Serif",
    "Microsoft Tai Le",
    "Microsoft YaHei",
    "Microsoft Yi Baiti",
    "MingLiU-ExtB",
    "Mongolian Baiti",
    "MS Gothic",
    "MV Boli",
    "Myanmar Text",
    "Nirmala UI",
    "Palatino Linotype",
    "Segoe MDL2 Assets",
    "Segoe Print",
    "Segoe Script",
    "Segoe UI",
    "Segoe UI Historic",
    "Segoe UI Emoji",
    "Segoe UI Symbol",
    "SimSun",
    "Sitka",
    "Sylfaen",
    "Symbol",
    "Tahoma",
    "Times New Roman",
    "Trebuchet MS",
    "Verdana",
    "Webdings",
    "Wingdings",
    "Yu Gothic",
    // macOS
    "American Typewriter",
    "Andale Mono",
    "Arial",
    "Arial Black",
    "Arial Narrow",
    "Arial Rounded MT Bold",
    "Arial Unicode MS",
    "Avenir",
    "Avenir Next",
    "Avenir Next Condensed",
    "Baskerville",
    "Big Caslon",
    "Bodoni 72",
    "Bodoni 72 Oldstyle",
    "Bodoni 72 Smallcaps",
    "Bradley Hand",
    "Brush Script MT",
    "Chalkboard",
    "Chalkboard SE",
    "Chalkduster",
    "Charter",
    "Cochin",
    "Comic Sans MS",
    "Copperplate",
    "Courier",
    "Courier New",
    "Didot",
    "DIN Alternate",
    "DIN Condensed",
    "Futura",
    "Geneva",
    "Georgia",
    "Gill Sans",
    "Helvetica",
    "Helvetica Neue",
    "Herculanum",
    "Hoefler Text",
    "Impact",
    "Lucida Grande",
    "Luminari",
    "Marker Felt",
    "Menlo",
    "Microsoft Sans Serif",
    "Monaco",
    "Noteworthy",
    "Optima",
    "Palatino",
    "Papyrus",
    "Phosphate",
    "Rockwell",
    "Savoye LET",
    "SignPainter",
    "Skia",
    "Snell Roundhand",
    "Tahoma",
    "Times",
    "Times New Roman",
    "Trattatello",
    "Trebuchet MS",
    "Verdana",
    "Zapfino",
  ].sort()
);
// Or you can use:
// const fabric = require("fabric").fabric;
//const canvas = new fabric.Canvas("my-fabric-canvas");
const App = () => {
  const canvasRef = useRef();
  const [canvas, setCanvas] = useState();
  const [canvasHistory, setCanvasHistory] = useState([initialState]);
  //const [canvasHistory, setCanvasHistory] = useState([]);
  const [canvasState, setCanvasState] = useState(initialState);
  //const [canvasState, setCanvasState] = useState();
  const [canvasTool, setCanvasTool] = useState("select");
  const [strokeWidth, setStrokeWidth] = useState(1);
  const [stroke, setStrokeColor] = useState("black");
  const [fill, setFillColor] = useState("");
  const [dashSize, setDashSize] = useState(0);
  const [gapSize, setGapSize] = useState(0);
  const [opacity, setOpacity] = useState(100);
  const [snap, setSnap] = useState(false);
  const [font, setFont] = useState("");

  var newShape = true;
  var Rectangle = null;
  var line = null;
  var origX, origY;

  const onObjectModified = useCallback(
    (e) => {
      const newCanvasState = e.target.canvas.toJSON();
      setCanvasState(newCanvasState);
      // Limit history depth
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
    },
    [setCanvasState, setCanvasHistory]
  );

  React.useEffect(() => {
    const fontAvailable = new Set();
    for (const font of fontCheck.values()) {
      if (document.fonts.check(`12px "${font}"`)) {
        fontAvailable.add(font);
        var x = document.getElementById("fonts");
        var option = document.createElement("option");
        option.text = font;
        x.add(option);
      }
      //console.log("Available Fonts:", [...fontAvailable.values()]);
    }
  }, []);
  React.useEffect(() => {}, [canvasState]);

  var isRedoing = false;
  var h = [];

  React.useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current);
    canvas.loadFromJSON(initialState);
    //canvas.on("object:added", onObjectModified);
    canvas.on("object:modified", onObjectModified);

    canvas.on("object:added", function () {
      if (!isRedoing) {
        h = [];
      }
      isRedoing = false;
    });
    setCanvas(canvas);
    return () => {
      canvas.dispose();
    };
  }, [canvasRef, onObjectModified, setCanvas]);

  function undo() {
    if (canvas._objects.length > 0) {
      h.push(canvas._objects.pop());
      canvas.renderAll();
    }
  }
  function redo() {
    if (h.length > 0) {
      isRedoing = true;
      canvas.add(h.pop());
    }
  }
  const moveHistory = useCallback(
    (step) => {
      const currentStateIndex = canvasHistory.indexOf(canvasState);
      const prevState = canvasHistory[currentStateIndex + step];
      canvas.loadFromJSON(prevState);
      setCanvasState(prevState);
    },
    [canvas, canvasState, canvasHistory, setCanvasState]
  );

  const onUndo = useCallback(() => moveHistory(-1), [moveHistory]);

  const onRedo = useCallback(() => moveHistory(1), [moveHistory]);

  const handleMouseClick = (event) => {
    //let o = canvas.getActiveObject();
    //console.log(o);
    if (canvasTool === "select") {
    }
    if (canvasTool === "line") {
      if (newShape) {
        var pointer = canvas.getPointer(event);
        var points = [pointer.x, pointer.y, pointer.x, pointer.y];
        line = new fabric.Line(points, {
          strokeWidth: strokeWidth,
          fill: fill,
          stroke: stroke,
          originX: "center",
          originY: "center",
        });
        canvas.add(line);
        newShape = false;
      } else {
        var pointer = canvas.getPointer(event);
        line.set({ x2: pointer.x, y2: pointer.y });

        canvas.setActiveObject(line);
        canvas.renderAll();
        newShape = true;
        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }

    if (canvasTool === "rect") {
      if (newShape) {
        var pointer = canvas.getPointer(event);
        origX = pointer.x;
        origY = pointer.y;
        Rectangle = new fabric.Rect({
          left: origX,
          top: origY,
          fill: fill,
          stroke: stroke,
          strokeWidth: strokeWidth,
          strokeDashArray: [dashSize, gapSize],
          opacity: opacity,
        });
        //console.log(Rectangle);
        canvas.add(Rectangle);
        setCanvas(canvas);
        //canvas.renderAll();
        //setRect((prevState) => (prevState = false));
        newShape = false;
        //setNewRectangle(false);
      } else {
        pointer = canvas.getPointer(event);
        if (origX > pointer.x) {
          Rectangle.set({ left: Math.abs(pointer.x) });
        }
        if (origY > pointer.y) {
          Rectangle.set({ top: Math.abs(pointer.y) });
        }
        Rectangle.set({ width: Math.abs(origX - pointer.x) });
        Rectangle.set({ height: Math.abs(origY - pointer.y) });
        canvas.setActiveObject(Rectangle);
        //setNewRectangle(true);
        newShape = true;
        // Render the Canvas
        canvas.renderAll();

        // Save the canvas to State variable
        const newCanvasState = canvas.toJSON();
        setCanvasState(newCanvasState);
        // Save state to History and Limit history depth to 32
        setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      }
    }
  };
  const handleMouseMove = (event) => {
    if (newShape === true && canvasTool != "pen") return;
    //canvas.renderAll();
    if (canvasTool === "rect") {
      var pointer = canvas.getPointer(event);
      if (origX > pointer.x) {
        Rectangle.set({ left: Math.abs(pointer.x) });
      }
      if (origY > pointer.y) {
        Rectangle.set({ top: Math.abs(pointer.y) });
      }
      Rectangle.set({ width: Math.abs(origX - pointer.x) });
      Rectangle.set({ height: Math.abs(origY - pointer.y) });

      canvas.renderAll();
    }
    if (canvasTool === "line") {
      var pointer = canvas.getPointer(event);
      line.set({ x2: pointer.x, y2: pointer.y });
      canvas.renderAll();
    }
  };

  const handleMouseDown = (event) => {
    if (canvas.isDrawingMode) {
      canvas.freeDrawingBrush.color = stroke;
      canvas.freeDrawingBrush.width = strokeWidth;
    }
  };

  const handleMouseUp = (event) => {
    console.log("mouseup");
    if (canvas.isDrawingMode) {
      canvas.renderAll();
      console.log("mouseup");
      //canvas.setActiveObject(activeShape);
      // Save the canvas to State variable
      // if (attributes.fill === undefined) {
      const newCanvasState = canvas.toJSON();
      setCanvasState(newCanvasState);
      // Save state to History and Limit history depth to 32
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      // }
    }
  };

  const setShapeAttributes = (attributes) => {
    var activeShape = canvas.getActiveObject();
    if (activeShape) {
      var attributes = attributes || {};
      activeShape.set({
        strokeWidth: attributes.strokeWidth || activeShape.strokeWidth,
        stroke:
          attributes.stroke === "nill"
            ? null
            : attributes.stroke || activeShape.stroke,
        fill: attributes.fill || activeShape.fill,
        opacity: attributes.opacity / 100 || activeShape.opacity,
        strokeDashArray:
          attributes.strokeDashArray || activeShape.strokeDashArray,
      });
      canvas.renderAll();
      canvas.setActiveObject(activeShape);
      // Save the canvas to State variable
      // if (attributes.fill === undefined) {
      const newCanvasState = canvas.toJSON();
      setCanvasState(newCanvasState);
      // Save state to History and Limit history depth to 32
      setCanvasHistory((history) => [...history, newCanvasState].slice(-32));
      // }
    }
  };
  function openHandler(colors) {
    setFillColor(colors.color);
  }
  function changeHandler(colors) {
    //setFillColor(colors.color);
    //setShapeAttributes({ fill: colors.color });
    //console.log(colors);
  }

  function closeHandler(colors) {
    if (colors.color !== fill) {
      setFillColor(colors.color);
      setShapeAttributes({ fill: colors.color });
    }
  }
  function openStrokeHandler(colors) {
    setStrokeColor(colors.color);
  }
  function closeStrokeHandler(colors) {
    if (colors.color !== stroke) {
      setStrokeColor(colors.color);
      setShapeAttributes({ stroke: colors.color });
    }
  }
  function freeHand(event) {
    setCanvasTool("pen");
    canvas.isDrawingMode = 1;
    canvas.freeDrawingBrush.color = stroke;
    canvas.freeDrawingBrush.width = strokeWidth;
    canvas.renderAll();
  }

  let url = "#";
  return (
    <div
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
      className="App"
    >
      <div className={tbStyles.propBar}>
        <a href={url}>
          <button className={tbStyles.buttontoolbar} onClick={undo} id="undo">
            <div className={tbStyles.tooltipDown}>
              <img src="images/undo2.png" alt="Undo"></img>
              <span className={tbStyles.tooltiptextDown}>Undo</span>
            </div>
          </button>
          <button className={tbStyles.buttontoolbar} onClick={redo} id="redo">
            <div className={tbStyles.tooltipDown}>
              <img src="images/redo2.png" alt="Redo"></img>
              <span className={tbStyles.tooltiptextDown}>Redo</span>
            </div>
          </button>
          <button className={tbStyles.buttontoolbar} onClick={onRedo} id="del">
            <div className={tbStyles.tooltipDown}>
              <img src="images/delete2.png" alt="Delete"></img>
              <span className={tbStyles.tooltiptextDown}>Delete</span>
            </div>
          </button>
        </a>
        <div className={tbStyles.dropdown}>
          <button className={tbStyles.buttontoolbar} id="wList">
            <img src="images/lineWidth2.png" alt="Line Width"></img>
            <div id="wlDropDown" className={tbStyles.dropdowncontent}>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(1);
                  setShapeAttributes({
                    strokeWidth: 1,
                  });
                }}
              >
                <img src="images/stroke1.png" alt="1"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(2);
                  setShapeAttributes({
                    strokeWidth: 2,
                  });
                }}
              >
                <img src="images/stroke2.png" alt="2"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(4);
                  setShapeAttributes({
                    strokeWidth: 4,
                  });
                }}
              >
                <img src="images/stroke4.png" alt="4"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(5);
                  setShapeAttributes({
                    strokeWidth: 5,
                  });
                }}
              >
                <img src="images/stroke5.png" alt="5"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(10);
                  setShapeAttributes({
                    strokeWidth: 10,
                  });
                }}
              >
                <img src="images/stroke10.png" alt="10"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(12);
                  setShapeAttributes({
                    strokeWidth: 12,
                  });
                }}
              >
                <img src="images/stroke12.png" alt="12"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(15);
                  setShapeAttributes({
                    strokeWidth: 15,
                  });
                }}
              >
                <img src="images/stroke15.png" alt="15"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(20);
                  setShapeAttributes({
                    strokeWidth: 20,
                  });
                }}
              >
                <img src="images/stroke20.png" alt="20"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setStrokeWidth(25);
                  setShapeAttributes({
                    strokeWidth: 25,
                  });
                }}
              >
                <img src="images/stroke25.png" alt="25"></img>
              </a>
            </div>
          </button>
        </div>
        <div className={tbStyles.dropdown}>
          <button className={tbStyles.buttontoolbar} id="sList">
            <img src="images/LineStyle2.png" alt="Line Style"></img>
            <div id="lsDropDown" className={tbStyles.dropdowncontent}>
              <a
                href={url}
                onClick={(e) => {
                  setDashSize(0);
                  setGapSize(0);
                  setShapeAttributes({
                    strokeDashArray: [0, 0],
                  });
                }}
              >
                <img src="images/lineStyle1.png" alt="1"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setDashSize(10);
                  setGapSize(5);
                  setShapeAttributes({
                    strokeDashArray: [10, 5],
                  });
                }}
              >
                <img src="images/lineStyle2png.png" alt="1"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setDashSize(10);
                  setGapSize(5);
                  setShapeAttributes({
                    strokeDashArray: [10, 5],
                  });
                }}
              >
                <img src="images/lineStyle3.png" alt="1"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setDashSize(20);
                  setGapSize(5);
                  setShapeAttributes({
                    strokeDashArray: [20, 5],
                  });
                }}
              >
                <img src="images/lineStyle4.png" alt="1"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setDashSize(20);
                  setGapSize(10);
                  setShapeAttributes({
                    strokeDashArray: [20, 10],
                  });
                }}
              >
                <img src="images/lineStyle5.png" alt="1"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setDashSize(5);
                  setGapSize(5);
                  setShapeAttributes({
                    strokeDashArray: [5, 5],
                  });
                }}
              >
                <img src="images/lineStyle6.png" alt="1"></img>
              </a>
              <a
                href={url}
                onClick={(e) => {
                  setDashSize(2);
                  setGapSize(2);
                  setShapeAttributes({
                    strokeDashArray: [2, 2],
                  });
                }}
              >
                <img src="images/lineStyle7.png" alt="1"></img>
              </a>
            </div>
          </button>
        </div>
        <button className={tbStyles.buttontoolbar} id="dropper">
          <div className={tbStyles.tooltipDown}>
            <img src="images/dropper2.png" alt="Pick a color"></img>
            <span className={tbStyles.tooltiptextDown}>Pick a color</span>
          </div>
        </button>
        &nbsp;Stroke&nbsp;
        <input
          type="number"
          id="strokePoint"
          value={strokeWidth}
          min="0"
          max="50"
          onChange={(e) => {
            setStrokeWidth(+e.target.value);
            setShapeAttributes({
              strokeWidth: +e.target.value,
            });
          }}
        ></input>
        &nbsp;Dash&nbsp;
        <input
          type="number"
          min="0"
          max="20"
          id="dashSize"
          value={dashSize}
          onChange={(e) => {
            setDashSize(+e.target.value);
            setShapeAttributes({
              strokeDashArray: [+e.target.value, +e.target.value],
            });
          }}
        ></input>
        &nbsp; Stroke &nbsp;{" "}
        <ColorPicker
          color={"#36c"}
          alpha={100}
          onOpen={openStrokeHandler}
          onChange={changeHandler}
          onClose={closeStrokeHandler}
          placement="bottomLeft"
          className="some-class"
        >
          <span className="rc-color-picker-trigger" />
        </ColorPicker>
        &nbsp;
        <button
          id="noStroke"
          onClick={(e) => {
            setStrokeColor("nill");
            setShapeAttributes({
              stroke: "nill",
            });
          }}
        >
          No Stroke
        </button>
        &nbsp; Fill &nbsp;&nbsp;{" "}
        <ColorPicker
          color={"#36c"}
          alpha={100}
          onOpen={openHandler}
          onChange={changeHandler}
          onClose={closeHandler}
          placement="topLeft"
          className="some-class"
        >
          <span className="rc-color-picker-trigger" />
        </ColorPicker>
        &nbsp;{" "}
        <button
          id="noFill"
          onClick={(e) => {
            setFillColor("nill");
            setShapeAttributes({
              fill: "nill",
            });
          }}
        >
          No Fill
        </button>
        &nbsp;Opacity&nbsp;
        <input
          type="range"
          id="opacityRange"
          value={opacity}
          onChange={(e) => {
            setOpacity(e.target.value);
            setShapeAttributes({
              opacity: e.target.value,
            });
          }}
        ></input>
        &nbsp; Angle snap:{" "}
        <input
          type="checkbox"
          id="chkSnap"
          onChange={(e) => {
            setSnap(e.target.value);
          }}
        ></input>
        &nbsp;Select Font &nbsp;
        <select
          name="fonts"
          id="fonts"
          onChange={(e) => {
            setFont(e.target.value);
          }}
        >
          {" "}
        </select>
        &nbsp;
