import React, { useRef, useState, useCallback } from "react";
import { fabric } from "fabric";
import reactCSS from "reactcss";
import { SketchPicker } from "react-color";
import ColorPicker from "rc-color-picker";
import "rc-color-picker/assets/index.css";

import "./styles.css";
import styles from "./rightMenu.module.css";
import tbStyles from "./toolbar.module.css";
//import { RgbaColorPicker } from "react-colorful";

//import "react-colorful/dist/index.css";

const initialState = {
  version: "3.6.3",
  objects: [
    {
      type: "rect",
      version: "3.6.3",
      originX: "left",
      originY: "top",
      left: 0,
      top: 0,
      width: 1,
      height: 1,
      fill: "black",
      stroke: null,
      strokeWidth: 1,
      strokeDashArray: null,
      strokeLineCap: "butt",
      strokeDashOffset: 0,
      strokeLineJoin: "miter",
      strokeMiterLimit: 4,
      scaleX: 1,
      scaleY: 1,
      angle: 0,
      flipX: false,
      flipY: false,
      opacity: 1,
      shadow: null,
      visible: false,
      clipTo: null,
      backgroundColor: "",
      fillRule: "nonzero",
      paintFirst: "fill",
      globalCompositeOperation: "source-over",
      transformMatrix: null,
      skewX: 0,
      skewY: 0,
      rx: 0,
      ry: 0,
      name: "shape-rect-n",
    },
  ],
};

const fontCheck = new Set(
  [
    // Windows 10
    "Arial",
    "Arial Black",
    "Bahnschrift",
    "Calibri",
    "Cambria",
    "Cambria Math",
    "Candara",
    "Comic Sans MS",
    "Consolas",
    "Constantia",
    "Corbel",
    "Courier New",
    "Ebrima",
    "Franklin Gothic Medium",
    "Gabriola",
    "Gadugi",
    "Georgia",
    "HoloLens MDL2 Assets",
    "Impact",
    "Ink Free",
    "Javanese Text",
    "Leelawadee UI",
    "Lucida Console",
    "Lucida Sans Unicode",
    "Malgun Gothic",
    "Marlett",
    "Microsoft Himalaya",
    "Microsoft JhengHei",
    "Microsoft New Tai Lue",
    "Microsoft PhagsPa",
    "Microsoft Sans Serif",
    "Microsoft Tai Le",
    "Microsoft YaHei",
    "Microsoft Yi Baiti",
    "MingLiU-ExtB",
    "Mongolian Baiti",
    "MS Gothic",
    "MV Boli",
    "Myanmar Text",
    "Nirmala UI",
    "Palatino Linotype",
    "Segoe MDL2 Assets",
    "Segoe Print",
    "Segoe Script",
    "Segoe UI",
    "Segoe UI Historic",
    "Segoe UI Emoji",
    "Segoe UI Symbol",
    "SimSun",
    "Sitka",
    "Sylfaen",
    "Symbol",
    "Tahoma",
    "Times New Roman",
    "Trebuchet MS",
    "Verdana",
    "Webdings",
    "Wingdings",
    "Yu Gothic",
    // macOS
    "American Typewriter",
    "Andale Mono",
    "Arial",
    "Arial Black",
    "Arial Narrow",
    "Arial Rounded MT Bold",
    "Arial Unicode MS",
    "Avenir",
    "Avenir Next",
    "Avenir Next Condensed",
    "Baskerville",
    "Big Caslon",
    "Bodoni 72",
    "Bodoni 72 Oldstyle",
    "Bodoni 72 Smallcaps",
    "Bradley Hand",
    "Brush Script MT",
    "Chalkboard",
    "Chalkboard SE",
    "Chalkduster",
    "Charter",
    "Cochin",
    "Comic Sans MS",
    "Copperplate",
    "Courier",
    "Courier New",
    "Didot",
    "DIN Alternate",
    "DIN Condensed",
    "Futura",
    "Geneva",
    "Georgia",
    "Gill Sans",
    "Helvetica",
    "Helvetica Neue",
    "Herculanum",
    "Hoefler Text",
    "Impact",
    "Lucida Grande",
    "Luminari",
    "Marker Felt",
    "Menlo",
    "Microsoft Sans Serif",
    "Monaco",
    "Noteworthy",
    "Optima",
    "Palatino",
    "Papyrus",
    "Phosphate",
    "Rockwell",
    "Savoye LET",
    "SignPainter",
    "Skia",
    "Snell Roundhand",
    "Tahoma",
    "Times",
    "Times New Roman",
    "Trattatello",
    "Trebuchet MS",
    "Verdana",
    "Zapfino",
  ].sort()
);
// Or you can use:
// const fabric = require("fabric").fabric;
//const canvas = new fabric.Canvas("my-fabric-canvas");
const App = () => {
  const canvasRef = useRef();
  const shapeCount = useRef(0);
  const isDrawing = useRef(false);
  const currentStroke = useRef("rgba(0,0,0,1)");
  const editPoly = useRef(true);
  const openPoly = useRef(null);
  const polyType = useRef("");
  //anchors
  const [canvas, setCanvas] = useState();
  const [canvasHistory, setCanvasHistory] = useState([initialState]);
  //const [canvasHistory, setCanvasHistory] = useState([]);
  const [canvasState, setCanvasState] = useState(initialState);
  //const [canvasState, setCanvasState] = useState();
  const [canvasTool, setCanvasTool] = useState("select");
  const [strokeWidth, setStrokeWidth] = useState(1);
  // const [stroke, setStrokeColor] = useState("black");
  const [stroke, setStrokeColor] = useState({
    r: "241",
    g: "112",
    b: "19",
    a: "1",
  });
  const [fill, setFillColor] = useState("black");
  const [dashSize, setDashSize] = useState(0);
  const [gapSize, setGapSize] = useState(0);
  const [opacity, setOpacity] = useState(100);
  const [snap, setSnap] = useState(false);
  const [font, setFont] = useState("");
  const [fontSize, setFontSize] = useState(30);
  const [fontWeight, setFontWeight] = useState("normal");
  const [fontStyle, setFontStyle] = useState("regular");
  const [textHighlight, setHighlight] = useState("yellow");

  const [showColorPicker, setShowColorPicker] = useState(false);
  //const [shapeCount, setShapeCount] = useState(0);

  const newShape = useRef(true);
  const anchors = useRef([]);
  const pointer = useRef();
  const line = useRef(null);
  const origX = useRef();
  const origY = useRef();
  const angle = useRef();
  const arrow = useRef(null);
  const selectedShape = useRef(null);
  const polyPoints = useRef([]);
  const rpolygon = useRef(null);
  const Rectangle = useRef(null);
  const circle = useRef(null);
  const ellipse = useRef(null);
  const polyLines = useRef([]);

  var currentOpenPanel = "None";
  var drawingObject = {};
  drawingObject.type = "";
  drawingObject.background = "";
