      //check if user clicked an object
      if (e.target) {
        var activeShape = canvas.getActiveObject();
        if (activeShape) {
          var rgba = activeShape.stroke.substr(5).split(")")[0].split(",");
          setStrokeColor(rgba);
          console.log(rgba);
          setFillColor(activeShape.fill);

